mapper.sql:
where 1=1

	
		and g_bespeak.id = #{id}
	
	
		and g_bespeak.bespeak_sn = #{bespeakSn}
	
	
		and g_bespeak.problem = #{problem}
	
	
		and g_bespeak.answer = #{answer}
	
	
		and g_bespeak.laborcost = #{laborcost}
	
	
		and g_bespeak.total_price = #{totalPrice}
	
	
		and g_bespeak.bespeak_date = #{bespeakDate}
	
	
		and g_bespeak.company_id = #{companyId}
	
	
		and g_bespeak.member_id = #{memberId}
	
	
		and g_bespeak.member_model_id = #{memberModelId}
	
	
		and g_bespeak.user_id = #{userId}
	
	
		and g_bespeak.state = #{state}
	
	
		and g_bespeak.create_date = #{createDate}
	
	
		and g_bespeak.modify_date = #{modifyDate}
	
	
		and  g_bespeak.bespeak_date <= #{afterDate}
	
	
		and g_bespeak.bespeak_date >= #{beforeDate}
	
	
		and b_member.phone = #{phone}

mapper.select:

select b.id,b.bespeak_sn,b.bespeak_date,b.state,b.laborcost,b.total_price,m.phone,m.name,mm.model_group_name,mm.mileage,mm.region,mm.license
	from g_bespeak b 
	left join b_member m on b.member_id = m.id 
	left join b_member_model mm on b.member_model_id = mm.id where 1=1
	



	${pager.orderCondition}


	${pager.queryCondition}

select * from ( select concat(mm.region,mm.license) aa,m.id,m.name,m.full_address,m.phone,m.create_date,mm.model_group_name,CONCAT(mm.region,mm.license) license , mm.vin,mm.mileage,m.sex,m.province,m.city,m.area,m.address,mm.model_id,mm.buy_date,mm.last_service_date,mm.chassis,mm.id as memberModelId, md.brand_id as shop_id,md.series_id,md.model_id as year,mm.engine_type,mm.engine_no,gear_box_type,gear_box_no,check_date,safe_date from b_member m left join b_member_model mm on m.id = mm.member_id and mm.default_flag = 1
left join v_model_detail md on md.model_id = mm.model_id where 1=1 ) m
${pager.orderCondition}
${pager.queryCondition}

mapper.sql:
where 1=1 

	
		and m.phone like  CONCAT('%',#{phone},'%')
	
	
		and m.aa like CONCAT('%',#{license},'%') 
	
	
		and m.vin = #{vin}
	
	
		and m.id = #{id}

mapper.select:
SELECT g_bespeak.bespeak_sn, g_bespeak.bespeak_date, g_bespeak.state, b_member.phone, b_member_model.model_group_name, CONCAT( b_member_model.region, b_member_model.license ) license FROM g_bespeak left join b_member on g_bespeak.member_id = b_member.id left join b_member_model on g_bespeak.member_model_id = b_member_model.id
WHERE 1=1 AND g_bespeak.company_id = #{companyId}
and g_bespeak.bespeak_date > #{nowDate}
${pager.orderCondition}
${pager.queryCondition}

SELECT g_bespeak.bespeak_sn, g_bespeak.answer, g_bespeak.bespeak_date, g_bespeak.create_date, b_company.name companyName FROM g_bespeak left join b_company on g_bespeak.company_id = b_company.id WHERE 1=1
${pager.orderCondition}
${pager.queryCondition}

select , b_member.phone, b_member_model.model_group_name, b_member_model.mileage from g_bespeak left join b_member on g_bespeak.member_id=b_member.id left join b_member_model on g_bespeak.member_model_id=b_member_model.id
${pager.orderCondition}
${pager.queryCondition}

select , b_member.phone, b_member_model.mileage, b_member_model.model_group_name modelGroupName from g_bespeak left join b_member on g_bespeak.member_id=b_member.id left join b_member_model on g_bespeak.member_model_id=b_member_model.id
${pager.orderCondition}
${pager.queryCondition}
